$(document).ready(function(){ 
    $(document).mouseenter(function(){
        $(".contenedor").hide();
    })

    $(document).mouseleave(function(){
        $(".contenedor").show();
    })

    $(".contenedor").css({"background-color" : "#ea97c38b"});
    $(".contenedor").css({"border-radius" : "20px"});
    $(".contenedor").css({"margin" : "30px"});






});